import React, { Component } from 'react';
import Avatar from '@material-ui/core/Avatar';

import Grid from '@material-ui/core/Grid';


 const styles = {
    bigAvatar: {
        margin: 10,
        width: 60,
        height: 60,
      }
};

class AccountInfo extends Component {

    state ={
        currentItem: '',
        list: [],
        bigAvatar: {
            margin: 10,
            width: 60,
            height: 60,
          }
    }

    handleToDo = event => {
        const name = event.target.name
        this.setState({ [name]: event.target.value })
    }

    addToDo = event => {
        event.preventDefault()
        const currentList = this.state.list
        currentList.push(this.state.currentItem)
        this.setState({list: currentList, currentItem: ''})
    }

    render() {
        
      return (
        <>
        <Grid container justify='center' alignItems='center'>
            <Avatar alt='userImage' src='./images/taylor.jpg' styles={this.state.bigAvatar} />
        </Grid>
        <Grid container justify='center' alignItems='center'>
        <form onSubmit={this.addToDo}>
            <input onChange={this.handleToDo} type='text' name='currentItem' value={this.state.currentItem} />
            <br />
            <button onClick={this.addToDo}>Add Item</button>
        </form>

        <ul>
            {this.state.list.map(item =>
            <li>{item}</li>)}
        </ul>
        </Grid>
        </>
      );
    }   
        
  };
  
  export default AccountInfo;
